import { Router } from "express";
import { authMiddleware } from "../middleware/auth.middleware.js";
import { deleteProfile } from "../controllers/profile/deleteProfile.controller.js";
import { getProfile } from "../controllers/profile/getProfile.controller.js";
import { Apidelay } from "../middleware/Apidelay.middleware.js";
import { editProfile } from "../controllers/profile/editProfile.controller.js";
import { upload } from "../middleware/multer.middleware.js";
import { getUserQuestions } from "../controllers/profile/getUserQuestion.controller.js";
import { getUserAnswers } from "../controllers/profile/getUserAnswer.controller.js";
const userProfileRouter = Router();
userProfileRouter.delete("/delete", Apidelay, authMiddleware, deleteProfile);
userProfileRouter.get("/get", Apidelay, authMiddleware, getProfile);
userProfileRouter.get("/get-questions", Apidelay, authMiddleware, getUserQuestions);
userProfileRouter.get("/get-answers", Apidelay, authMiddleware, getUserAnswers);
userProfileRouter.put("/edit", Apidelay, upload.single("image"), authMiddleware, editProfile);
export default userProfileRouter;
